import { gql, useQuery } from '@apollo/client';
import { css } from '@emotion/css';
import { divide } from 'lodash-es';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import { Button, Container, Divider, Dropdown, Grid, Header, Icon, Image, List, Menu, Segment, Table } from 'semantic-ui-react';
import './PokemonDetail.scss';

function PokemonDetail(){
    const [width, setWidth] = useState(window.innerWidth);

    let{name}=useParams();
    // const [pokemon,setPokemon]=useState();
    
    const POKEMON_QUERY=gql`
      query pokemon($name: String!) {
          pokemon(name: $name) {
            id
            name
            sprites {
              front_default
              front_shiny
            }        
            stats{
              base_stat

              stat{
                name
              }
              
            }
            moves {
              move {
                name
              }
            }
            types {
              type {
                name
              }
            }
          }
        }
    `;
    const{loading , error, data}=useQuery(POKEMON_QUERY,{
      variables:{
          name:name
      }
    });
    

    // var mobile = (width <= 768);
    if(loading)return(
      <div></div>
    );
    console.log(data);
    let pokemon=data.pokemon;
    return(
      <div className={css`
        display:flex;
        width:100%;
        height:100%;
        flex-direction:column;
        align-items:center;
        justify-content:center;
      `}>
        <h1>{pokemon.name}</h1>
        
        <div className="image-container">
          <img src={pokemon.sprites.front_default} alt="Failed to load" />
          <img src={pokemon.sprites.front_shiny} alt="Failed to load" />
        </div>
        <div className={css`
          width:300px;
          display:flex;
          justify-content:center;
        `}> 
        <Table basic='very' celled 
        collapsing
        >
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell><p className={css`text-align: right;`}>Stats</p></Table.HeaderCell>
              <Table.HeaderCell><p>Value</p></Table.HeaderCell>
            </Table.Row>
          </Table.Header>

          <Table.Body>
            <Table.Row>
              <Table.Cell>
                <Header as='h4' className={css`
                    text-align: right;
                    width:100%;
                    `}>
                  <Header.Content>
                    <h1>{pokemon.stats[0].stat.name}</h1>
                    
                  </Header.Content>
                </Header>
              </Table.Cell>
              <Table.Cell>{pokemon.stats[0].base_stat}</Table.Cell>
            </Table.Row>
            <Table.Row>
              <Table.Cell>
                <Header className={css`
                    text-align: right;
                    width:100%;
                    `} as='h4'>
                  <Header.Content>
                    <h3>
                    {pokemon.stats[1].stat.name}
                    </h3>

                  </Header.Content>
                </Header>
              </Table.Cell>
              <Table.Cell>{pokemon.stats[1].base_stat}</Table.Cell>
            </Table.Row>
            <Table.Row>
              <Table.Cell>
                <Header className={css`
                    text-align: right;
                    width:100%;
                    `} as='h4' >
                  <Header.Content>
                  <h3>{pokemon.stats[2].stat.name}</h3>
                  </Header.Content>
                </Header>
              </Table.Cell>
              <Table.Cell>{pokemon.stats[2].base_stat}</Table.Cell>
            </Table.Row>
            <Table.Row>
              <Table.Cell>
                <Header className={css`
                    text-align: right;
                    width:100%;
                    `} as='h4' >
                  <Header.Content>
                  <h3>{pokemon.stats[3].stat.name}</h3>
                  </Header.Content>
                </Header>
              </Table.Cell>
              <Table.Cell>{pokemon.stats[3].base_stat}</Table.Cell>
            </Table.Row>
            <Table.Row>
              <Table.Cell>
                <Header className={css`
                    text-align: right;
                    width:100%;
                    `} as='h4' >
                  <Header.Content>
                  <h3>{pokemon.stats[4].stat.name}</h3>
                  </Header.Content>
                </Header>
              </Table.Cell>
              <Table.Cell>{pokemon.stats[4].base_stat}</Table.Cell>
            </Table.Row>

            <Table.Row>
              <Table.Cell>
                <Header className={css`
                    text-align: right;
                    width:100%;
                    `} as='h4' >
                  <Header.Content>
                  <h3>{pokemon.stats[5].stat.name}</h3>
                  </Header.Content>
                </Header>
              </Table.Cell>
              <Table.Cell>{pokemon.stats[5].base_stat}</Table.Cell>
            </Table.Row>
          </Table.Body>
        </Table>
        </div>
      </div>
    );  
    
}

export default PokemonDetail
