import { useState } from "react";
import { Route, Switch } from "react-router";
import { Button, Container, Menu, Segment, Visibility } from "semantic-ui-react";
import ListPage from "../ListPage/ListPage";


function Desktop(){

    const [fixed,setFixed]=useState(false);

    const [activePage, setActive]=useState(1);
    function hideFixedMenu ()  {
        setFixed(false)
        
    }

    function showFixedMenu ()  {
        setFixed(true)
        
    }
    return (<div>
      <Visibility
      once={false}
      onBottomPassed={showFixedMenu}
      onBottomPassedReverse={hideFixedMenu}
    >
      <Segment
        inverted
        textAlign='center'
        style={{ minHeight: 80, padding: '1em 0em' }}
        vertical
      >
        <Menu
          fixed={fixed ? 'top' : null}
          inverted={!fixed}
          pointing={!fixed}
          secondary={!fixed}
          size='large'
        >
          <Container>
            <Menu.Item as='a' onClick={()=>{setActive(1)}} active={activePage===1} href='./'>List</Menu.Item>
            <Menu.Item as='a' onClick={()=>{setActive(2)}} active={activePage===2} href='./Search'>Search</Menu.Item>
            <Menu.Item as='a' onClick={()=>{setActive(3)}} active={activePage===3} href='./asd'>Favourites</Menu.Item>
          </Container>
        </Menu>
      </Segment>
      
    </Visibility>
      <Switch>
          <Route path='/asd'>
            testing
          </Route>
          <Route path='/'>
            <ListPage/>
          </Route>
          
        </Switch>
    </div>
    )
}


export default Desktop
