import { gql, useQuery } from '@apollo/client';
import { css } from '@emotion/css';
import { divide } from 'lodash-es';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import { Button, Container, Divider, Dropdown, Grid, Header, Icon, Image, List, Menu, Segment, Table } from 'semantic-ui-react';
import './PokemonDetail.scss';

function PokemonDetail(){
    const [width, setWidth] = useState(window.innerWidth);

    let{name}=useParams();
    // const [pokemon,setPokemon]=useState();
    
    const POKEMON_QUERY=gql`
      query pokemon($name: String!) {
          pokemon(name: $name) {
            id
            name
            sprites {
              front_default
              front_shiny
            }        
            stats{
              base_stat

              stat{
                name
              }
              
            }
            moves {
              move {
                name
              }
            }
            types {
              type {
                name
              }
            }
          }
        }
    `;
    const{loading , error, data}=useQuery(POKEMON_QUERY,{
      variables:{
          name:name
      }
    });
    

    // var mobile = (width <= 768);
    if(loading)return(
      <div></div>
    );
    console.log(data);
    let pokemon=data.pokemon;
    return(
      <div className={css`
        display:flex;
        width:100%;
        height:100%;
        flex-direction:column;
        align-items:center;
        justify-content:center;
      `}>
        <h1>{pokemon.name}</h1>
        
        <div className="image-container">
          <img src={pokemon.sprites.front_default} alt="Failed to load" />
          <img src={pokemon.sprites.front_shiny} alt="Failed to load" />
        </div>
        <Table basic='very' celled collapsing>
    <Table.Header>
      <Table.Row>
        <Table.HeaderCell>Stats</Table.HeaderCell>
        <Table.HeaderCell>Value</Table.HeaderCell>
      </Table.Row>
    </Table.Header>

    <Table.Body>
      <Table.Row>
        <Table.Cell>
          <Header as='h4' image>
            <Header.Content>
              {pokemon.stats[0].stat.name}
            </Header.Content>
          </Header>
        </Table.Cell>
        <Table.Cell>{pokemon.stats[0].base_stat}</Table.Cell>
      </Table.Row>
      <Table.Row>
        <Table.Cell>
          <Header as='h4' image>
            <Header.Content>
              {pokemon.stats[1].stat.name}
            </Header.Content>
          </Header>
        </Table.Cell>
        <Table.Cell>{pokemon.stats[1].base_stat}</Table.Cell>
      </Table.Row>
      <Table.Row>
        <Table.Cell>
          <Header as='h4' image>
            <Image src='/images/avatar/small/lindsay.png' rounded size='mini' />
            <Header.Content>
            {pokemon.stats[2].stat.name}

            </Header.Content>
          </Header>
        </Table.Cell>
        <Table.Cell>{pokemon.stats[2].base_stat}</Table.Cell>
      </Table.Row>
      <Table.Row>
        <Table.Cell>
          <Header as='h4' image>
            <Header.Content>

            {pokemon.stats[3].stat.name}

            </Header.Content>
          </Header>
        </Table.Cell>
        <Table.Cell>{pokemon.stats[3].base_stat}</Table.Cell>
      </Table.Row>
      
      <Table.Row>
        <Table.Cell>
          <Header as='h4' image>
            <Header.Content>

            </Header.Content>
          </Header>
        </Table.Cell>
        <Table.Cell>{pokemon.stats[4].base_stat}</Table.Cell>
      </Table.Row>

      <Table.Row>
        <Table.Cell>
          <Header as='h4' image>
            <Header.Content>
            {pokemon.stats[5].stat.name}

            </Header.Content>
          </Header>
        </Table.Cell>
        <Table.Cell>{pokemon.stats[5].base_stat}</Table.Cell>
      </Table.Row>
    </Table.Body>
  </Table>
      </div>
    );  
    
}

export default PokemonDetail
