import {Link} from 'react-router-dom';
import './PokemonCard.scss';
import { Card, Image, Placeholder } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
function PokemonCard(props){
    console.log(props);

    let poke=props.pokemon;
    let loading=poke==='loading';
    // let loading=true;
    return (
    
    
    
        <Card key={poke.name} className="bg-surface" >


        
        {loading?
            
            <Placeholder className='poke-image'>
                <Placeholder.Image square className='poke-image'/>
            </Placeholder>
            :
            <Link className="card bg-surface"  style={{ textDecoration:'none'}}>
                <Image src={poke.image} className='poke-image' alt='Failed to load'/>
                </Link>
            }

    <Card.Content className="bg-surface card-content text-center">
        {loading?
            <Placeholder className="ph">
                <Placeholder.Header className="ph">
                    <Placeholder.Line className="ph" length='full' />
                </Placeholder.Header>
            </Placeholder>
            :
            <div className="txt-primary card-content bg-surface">
                <Card.Header className="bg-surface">{poke.name}</Card.Header>
            </div> 
        }
        </Card.Content>
            </Card>
        // </Link>
   
    

    )
}




export default PokemonCard
