import { gql, useQuery } from '@apollo/client';
import { css } from '@emotion/css';
import { divide } from 'lodash-es';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import { Button, Container, Divider, Dropdown, Grid, Header, Icon, Image, List, Menu, Segment, Table } from 'semantic-ui-react';
import './PokemonDetail.scss';

function PokemonDetail(){
    const [width, setWidth] = useState(window.innerWidth);

    let{name}=useParams();
    // const [pokemon,setPokemon]=useState();
    
    const POKEMON_QUERY=gql`
      query pokemon($name: String!) {
          pokemon(name: $name) {
            id
            name
            types{
              type{
                name
              }
            }
            sprites {
              front_default
              front_shiny
            }        
            stats{
              base_stat

              stat{
                name
              }
              
            }
            moves {
              move {
                name
              }
            }
            types {
              type {
                name
              }
            }
          }
        }
    `;
    const{loading , error, data}=useQuery(POKEMON_QUERY,{
      variables:{
          name:name
      }
    });
    

    // var mobile = (width <= 768);
    if(loading)return(
      <div></div>
    );
    console.log(data);
    let pokemon=data.pokemon;
    return(
      <div className={css`
        display:flex;
        width:100%;
        height:100%;
        flex-direction:column;
        align-items:center;
        justify-content:center;
      `}>
        
        <div className="image-container">
          <img src={pokemon.sprites.front_default} alt="Failed to load" />
          <img src={pokemon.sprites.front_shiny} alt="Failed to load" />
        </div>
        <div className={css`
          display:flex;
          width: min(600px,90vw);
          flex-direction:row;
          align-item:center;
          justify-content:center;
        `}>
          <div className={css`
            width:100%;
          `}>
            <h3>Pokemon
              <br/>
              <div className={css`
              display:flex;
              flex-direction:row;
              `}>
                {pokemon.name} 
                
                {pokemon.types?.map((type)=>{
                    return (
                    
                    
                    <div className={type.type.name+' '+css`
                      border-radius:20vw;
                    `}>

                      {type.type.name}
                    </div>)
                })}

                <div>
                </div>
              </div>
              

            </h3>
          </div>
        </div>
        <div className={css`
          display:flex;
          width: min(600px,90vw);
          flex-direction:row;
          align-item:center;
          justify-content:center;
          text-align:center;
        `}>

          <Table celled >
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell><h3>Stats</h3></Table.HeaderCell>
                <Table.HeaderCell><h3>Value</h3></Table.HeaderCell>
              </Table.Row>
            </Table.Header>

            <Table.Body>
              <Table.Row>
                <Table.Cell>
                  <Header as='h4' image >
                        
                    <Header.Content>
                      <h3>{pokemon.stats[0].stat.name}</h3>
                      
                    </Header.Content>
                  </Header>
                </Table.Cell>
                <Table.Cell><h3>{pokemon.stats[0].base_stat}</h3></Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  <Header  as='h4'>
                    <Header.Content>
                      <h3>
                      {pokemon.stats[1].stat.name}
                      </h3>

                    </Header.Content>
                  </Header>
                </Table.Cell>
                <Table.Cell><h3>{pokemon.stats[1].base_stat}</h3>
                </Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  <Header  as='h4' >
                    <Header.Content>
                    <h3>{pokemon.stats[2].stat.name}</h3>
                    </Header.Content>
                  </Header>
                </Table.Cell>
                <Table.Cell><h3>{pokemon.stats[2].base_stat}</h3></Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  <Header as='h4' >
                    <Header.Content>
                    <h3>{pokemon.stats[3].stat.name}</h3>
                    </Header.Content>
                  </Header>
                </Table.Cell>
                <Table.Cell><h3>{pokemon.stats[3].base_stat}</h3></Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  <Header as='h4' >
                    <Header.Content>
                    <h3>{pokemon.stats[4].stat.name}</h3>
                    </Header.Content>
                  </Header>
                </Table.Cell>
                <Table.Cell><h3>{pokemon.stats[4].base_stat}</h3></Table.Cell>
              </Table.Row>

              <Table.Row>
                <Table.Cell>
                  <Header as='h4' >
                    <Header.Content>
                    <h3>{pokemon.stats[5].stat.name}</h3>
                    </Header.Content>
                  </Header>
                </Table.Cell>
                <Table.Cell><h3>{pokemon.stats[5].base_stat}</h3></Table.Cell>
              </Table.Row>
            </Table.Body>
          </Table>
          <div className={css`
            
          `}>
          </div>
        </div>
        
        </div>
    );  
    
}

export default PokemonDetail
