import {Link} from 'react-router-dom';
import './PokemonCard.scss';
import { Placeholder } from 'semantic-ui-react'
function PokemonCard(props){
    console.log(props);

    let poke=props.pokemon;
    let loading=poke==='loading';
    if(poke==='loading'){
        return(
            <Placeholder className="card text-white bg-dark mb-3" style={{ textDecoration:'none'}}>
                <Placeholder.Header image className='card-img-top poke-image'/>
                <Placeholder.Paragraph className="card-body">
                    <Placeholder.Line className='card-title' />
                </Placeholder.Paragraph>
            </Placeholder>            

        )
    }
    return (
    //     <Placeholder className="card text-white bg-dark mb-3" >
    //     <Placeholder.Header image className='card-img-top poke-image'/>
    //     <Placeholder.Paragraph className="card-body">
    //         <Placeholder.Line className='card-title' />
    //     </Placeholder.Paragraph>
    // </Placeholder>        

            <Link className="card text-white bg-dark mb-3" style={{ textDecoration:'none'}}>
                {loading?
                    <Placeholder>

                    </Placeholder>:
                    <div>
                        <img className='card-img-top poke-image' src={poke.image} alt='Failed to load'/>
                    <div className="card-body">
                        <h5 className='card-title' >{poke.name}</h5>
                    </div>
                    </div>
                }

            </Link>            

    )
}




export default PokemonCard
