import {Link} from 'react-router-dom';
import './PokemonCard.scss';
import { Card, Image, Placeholder } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
function PokemonCard(props){
    console.log(props);

    let poke=props.pokemon;
    let loading=poke==='loading';
    // let loading=true;
    return (
    
    
        <Link className="card text-white bg-dark mb-3"  style={{ textDecoration:'none'}}>
        <Card key={poke.name} className="bg-surface" >


        
        {loading?
            
            <Placeholder className='poke-image'>
                <Placeholder.Image square className='poke-image'/>
            </Placeholder>
            :
                <Image src={poke.image} className='poke-image' alt='Failed to load'/>
            }

    <Card.Content>
        {loading?
            <Placeholder>
                <Placeholder.Header>
                    <Placeholder.Line length='very short' />
                    <Placeholder.Line length='medium' />
                </Placeholder.Header>
                <Placeholder.Paragraph>
                    <Placeholder.Line length='short' />
                </Placeholder.Paragraph>
            </Placeholder>
            :
            <div className="txt-primary">
                <Card.Header>{poke.name}</Card.Header>
            </div> 
        }
        </Card.Content>
            </Card>
        </Link>
   
    

    )
}




export default PokemonCard
