import { useState } from "react";
import { Button, Container, Menu, Segment, Visibility } from "semantic-ui-react";
import ListPageContent from "./ListPageContent";

function Desktop(){

    const [fixed,setFixed]=useState(false);


    function hideFixedMenu ()  {
        setFixed(false)
    }

    function showFixedMenu ()  {
        setFixed(true)
    }
    return (<Visibility
      once={false}
      onBottomPassed={showFixedMenu}
      onBottomPassedReverse={hideFixedMenu}
    >
      <Segment
        inverted
        textAlign='center'
        style={{ minHeight: 80, padding: '1em 0em' }}
        vertical
      >
        <Menu
          fixed={fixed ? 'top' : null}
          inverted={!fixed}
          pointing={!fixed}
          secondary={!fixed}
          size='large'
        >
          <Container>
            <Menu.Item as='a' active href='//google.com'>
              List
            </Menu.Item>
            <Menu.Item as='a' href='//google.com'>Search</Menu.Item>
            <Menu.Item as='a' href='//google.com'>Favourites</Menu.Item>
          </Container>
        </Menu>
      </Segment>
      <Switch>
          <Route path='/'>
            <ListPageContent/>
          </Route>
        </Switch>
    </Visibility>
    )
}


export default Desktop
