import {Link} from 'react-router-dom';
import './PokemonCard.scss';
import { Card, Image, Placeholder } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
function PokemonCard(props){
    console.log(props);

    let poke=props.pokemon;
    let loading=poke==='loading';
    // let loading=true;
    return (
    
    
    <Link className="card bg-surface" style={{ textDecoration:'none'}}>
        <Card key={poke.name} className="full bg-surface" >


        
            {loading?
            
            <Placeholder className='poke-image'>
                <Placeholder.Image square className='poke-image'/>
            </Placeholder>
            :
                <Image src={poke.image} className='poke-image' alt='Failed to load'/>
            }

            <Card.Content className="bg-surface full ph text-center">
                    {loading?
                        <Placeholder className="ph full">
                            <Placeholder.Header className="ph">
                                <Placeholder.Line className="ph" length='long' />
                            </Placeholder.Header>
                        </Placeholder>
                        :
                        <div className="full txt-primary">
                            <Card.Header className="full"><h4>{poke.name}</h4></Card.Header>
                        </div> 
                    }
                </Card.Content>
            </Card>
        </Link>
   
    

    )
}




export default PokemonCard
