import { gql, useQuery } from '@apollo/client';
import { css } from '@emotion/css';
import { divide } from 'lodash-es';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import { Button, Container, Divider, Grid, Header, Icon, Image, List, Segment } from 'semantic-ui-react';
import './PokemonDetail.scss';

function PokemonDetail(){
    const [width, setWidth] = useState(window.innerWidth);

    let{name}=useParams();
    // const [pokemon,setPokemon]=useState();
    
    const POKEMON_QUERY=gql`
      query pokemon($name: String!) {
          pokemon(name: $name) {
            id
            name
            sprites {
              front_default
              front_shiny
            }
            moves {
              move {
                name
              }
            }
            types {
              type {
                name
              }
            }
          }
        }
    `;
    const{loading , error, data}=useQuery(POKEMON_QUERY,{
      variables:{
          name:name
      }
    });
    

    // var mobile = (width <= 768);
    if(loading)return(
      <div></div>
    );
    console.log(data);
    let pokemon=data.pokemon;
    return(
      <div className={css`
        display:flex;
        width:100%;
        height:100%;
        flex-direction:column;
        align-items:center;
        justify-content:center;
      `}>
        <h1>{pokemon.name}</h1>
        
        <div className="image-container">
          <img src={pokemon.sprites.front_default} alt="Failed to load" />
          <img src={pokemon.sprites.front_shiny} alt="Failed to load" />
        </div>
        
	
	<div class="container">
		<div class="card">
			<div class="container-fliud">
				<div class="wrapper row">
					<div class="preview col-md-6">
						
						<div class="preview-pic tab-content">
						  <div class="tab-pane active" id="pic-1"><img src="http://placekitten.com/400/252" /></div>
						  <div class="tab-pane" id="pic-2"><img src="http://placekitten.com/400/252" /></div>
						  <div class="tab-pane" id="pic-3"><img src="http://placekitten.com/400/252" /></div>
						  <div class="tab-pane" id="pic-4"><img src="http://placekitten.com/400/252" /></div>
						  <div class="tab-pane" id="pic-5"><img src="http://placekitten.com/400/252" /></div>
						</div>
						<ul class="preview-thumbnail nav nav-tabs">
						  <li class="active"><a data-target="#pic-1" data-toggle="tab"><img src="http://placekitten.com/200/126" /></a></li>
						  <li><a data-target="#pic-2" data-toggle="tab"><img src="http://placekitten.com/200/126" /></a></li>
						  <li><a data-target="#pic-3" data-toggle="tab"><img src="http://placekitten.com/200/126" /></a></li>
						  <li><a data-target="#pic-4" data-toggle="tab"><img src="http://placekitten.com/200/126" /></a></li>
						  <li><a data-target="#pic-5" data-toggle="tab"><img src="http://placekitten.com/200/126" /></a></li>
						</ul>
						
					</div>
					<div class="details col-md-6">
						<h3 class="product-title">men's shoes fashion</h3>
						<div class="rating">
							<div class="stars">
								<span class="fa fa-star checked"></span>
								<span class="fa fa-star checked"></span>
								<span class="fa fa-star checked"></span>
								<span class="fa fa-star"></span>
								<span class="fa fa-star"></span>
							</div>
							<span class="review-no">41 reviews</span>
						</div>
						<p class="product-description">Suspendisse quos? Tempus cras iure temporibus? Eu laudantium cubilia sem sem! Repudiandae et! Massa senectus enim minim sociosqu delectus posuere.</p>
						<h4 class="price">current price: <span>$180</span></h4>
						<p class="vote"><strong>91%</strong> of buyers enjoyed this product! <strong>(87 votes)</strong></p>
						<h5 class="sizes">sizes:
							<span class="size" data-toggle="tooltip" title="small">s</span>
							<span class="size" data-toggle="tooltip" title="medium">m</span>
							<span class="size" data-toggle="tooltip" title="large">l</span>
							<span class="size" data-toggle="tooltip" title="xtra large">xl</span>
						</h5>
						<h5 class="colors">colors:
							<span class="color orange not-available" data-toggle="tooltip" title="Not In store"></span>
							<span class="color green"></span>
							<span class="color blue"></span>
						</h5>
						<div class="action">
							<button class="add-to-cart btn btn-default" type="button">add to cart</button>
							<button class="like btn btn-default" type="button"><span class="fa fa-heart"></span></button>
						</div>
					</div>
				</div>
			</div>
		</div>
	</div>
      </div>
    );  
    
}

export default PokemonDetail
