import { gql, useQuery } from '@apollo/client';
import { css } from '@emotion/css';
import { divide } from 'lodash-es';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import { Button, Container, Divider, Dropdown, Grid, Header, Icon, Image, List, Menu, Segment, Table } from 'semantic-ui-react';
import './PokemonDetail.scss';

function PokemonDetail(){
    const [width, setWidth] = useState(window.innerWidth);

    let{name}=useParams();
    // const [pokemon,setPokemon]=useState();
    
    const POKEMON_QUERY=gql`
      query pokemon($name: String!) {
          pokemon(name: $name) {
            id
            name
            sprites {
              front_default
              front_shiny
            }        
            stats{
              base_stat

              stat{
                name
              }
              
            }
            moves {
              move {
                name
              }
            }
            types {
              type {
                name
              }
            }
          }
        }
    `;
    const{loading , error, data}=useQuery(POKEMON_QUERY,{
      variables:{
          name:name
      }
    });
    

    // var mobile = (width <= 768);
    if(loading)return(
      <div></div>
    );
    console.log(data);
    let pokemon=data.pokemon;
    return(
      <div className={css`
        display:flex;
        width:100%;
        height:100%;
        flex-direction:column;
        align-items:center;
        justify-content:center;
      `}>
        
        <div className="image-container">
          <img src={pokemon.sprites.front_default} alt="Failed to load" />
          <img src={pokemon.sprites.front_shiny} alt="Failed to load" />
        </div>
        <h1>{pokemon.name}</h1>
        <div className={css`
          display:flex;
          width:100%;
          flex-direction:row;
          align-item:center;
          justify-content:center;
          text-align:center;
        `}>
          <Table celled collapsing>
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell><h3>Stats</h3></Table.HeaderCell>
                <Table.HeaderCell><h3>Value</h3></Table.HeaderCell>
              </Table.Row>
            </Table.Header>

            <Table.Body>
              <Table.Row>
                <Table.Cell>
                  <Header as='h4' image >
                        
                    <Header.Content>
                      <h3>{pokemon.stats[0].stat.name}</h3>
                      
                    </Header.Content>
                  </Header>
                </Table.Cell>
                <Table.Cell><h3>{pokemon.stats[0].base_stat}</h3></Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  <Header  as='h4'>
                    <Header.Content>
                      <h3>
                      {pokemon.stats[1].stat.name}
                      </h3>

                    </Header.Content>
                  </Header>
                </Table.Cell>
                <Table.Cell><h3>{pokemon.stats[1].base_stat}</h3>
                </Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  <Header  as='h4' >
                    <Header.Content>
                    <h3>{pokemon.stats[2].stat.name}</h3>
                    </Header.Content>
                  </Header>
                </Table.Cell>
                <Table.Cell><h3>{pokemon.stats[2].base_stat}</h3></Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  <Header as='h4' >
                    <Header.Content>
                    <h3>{pokemon.stats[3].stat.name}</h3>
                    </Header.Content>
                  </Header>
                </Table.Cell>
                <Table.Cell><h3>{pokemon.stats[3].base_stat}</h3></Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  <Header as='h4' >
                    <Header.Content>
                    <h3>{pokemon.stats[4].stat.name}</h3>
                    </Header.Content>
                  </Header>
                </Table.Cell>
                <Table.Cell><h3>{pokemon.stats[4].base_stat}</h3></Table.Cell>
              </Table.Row>

              <Table.Row>
                <Table.Cell>
                  <Header as='h4' >
                    <Header.Content>
                    <h3>{pokemon.stats[5].stat.name}</h3>
                    </Header.Content>
                  </Header>
                </Table.Cell>
                <Table.Cell><h3>{pokemon.stats[5].base_stat}</h3></Table.Cell>
              </Table.Row>
            </Table.Body>
          </Table>
        
        </div>
        <Grid columns={3} divided='vertically'>
      <Grid.Row>
        <Grid.Column>
          <p />dsa
        </Grid.Column>
        <Grid.Column>
          <p />dsad
        </Grid.Column>
        <Grid.Column>
          <p />
        </Grid.Column>
      </Grid.Row>
      <Grid.Row>
        <Grid.Column>
          <p />
        </Grid.Column>
        <Grid.Column>
          <p />
        </Grid.Column>
        <Grid.Column>
          <p />
        </Grid.Column>
      </Grid.Row>
    </Grid>

        </div>
    );  
    
}

export default PokemonDetail
