import { gql, useQuery } from '@apollo/client';
import { css } from '@emotion/css';
import { divide } from 'lodash-es';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import { Button, Container, Divider, Dropdown, Grid, Header, Icon, Image, List, Menu, Segment } from 'semantic-ui-react';
import './PokemonDetail.scss';

function PokemonDetail(){
    const [width, setWidth] = useState(window.innerWidth);

    let{name}=useParams();
    // const [pokemon,setPokemon]=useState();
    
    const POKEMON_QUERY=gql`
      query pokemon($name: String!) {
          pokemon(name: $name) {
            id
            name
            sprites {
              front_default
              front_shiny
            }
            moves {
              move {
                name
              }
            }
            types {
              type {
                name
              }
            }
          }
        }
    `;
    const{loading , error, data}=useQuery(POKEMON_QUERY,{
      variables:{
          name:name
      }
    });
    

    // var mobile = (width <= 768);
    if(loading)return(
      <div></div>
    );
    console.log(data);
    let pokemon=data.pokemon;
    return(
      <div className={css`
        display:flex;
        width:100%;
        height:100%;
        flex-direction:column;
        align-items:center;
        justify-content:center;
      `}>
        <h1>{pokemon.name}</h1>
        
        <div className="image-container">
          <img src={pokemon.sprites.front_default} alt="Failed to load" />
          <img src={pokemon.sprites.front_shiny} alt="Failed to load" />
        </div>
        <Menu fixed='top' inverted>
      <Container>
        <Menu.Item as='a' header>
          <Image size='mini' src='/logo.png' style={{ marginRight: '1.5em' }} />
          Project Name
        </Menu.Item>
        <Menu.Item as='a'>Home</Menu.Item>

        <Dropdown item simple text='Dropdown'>
          <Dropdown.Menu>
            <Dropdown.Item>List Item</Dropdown.Item>
            <Dropdown.Item>List Item</Dropdown.Item>
            <Dropdown.Divider />
            <Dropdown.Header>Header Item</Dropdown.Header>
            <Dropdown.Item>
              <i className='dropdown icon' />
              <span className='text'>Submenu</span>
              <Dropdown.Menu>
                <Dropdown.Item>List Item</Dropdown.Item>
                <Dropdown.Item>List Item</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown.Item>
            <Dropdown.Item>List Item</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </Container>
    </Menu>

    <Container text style={{ marginTop: '7em' }}>
      <Header as='h1'>Semantic UI React Fixed Template</Header>
      <p>This is a basic fixed menu template using fixed size containers.</p>
      <p>
        A text container is used for the main container, which is useful for single column layouts.
      </p>

      <Image src={pokemon.sprites.front_default} style={{ marginTop: '2em' }} />
      saffd
      <Image src={pokemon.sprites.front_default} style={{ marginTop: '2em' }} />
      <Image src={pokemon.sprites.front_default} style={{ marginTop: '2em' }} />
      <Image src={pokemon.sprites.front_default} style={{ marginTop: '2em' }} />
      <Image src={pokemon.sprites.front_default} style={{ marginTop: '2em' }} />
      <Image src={pokemon.sprites.front_default} style={{ marginTop: '2em' }} />
      <Image src={pokemon.sprites.front_default} style={{ marginTop: '2em' }} />
    </Container>

    <Segment inverted vertical style={{ margin: '5em 0em 0em', padding: '5em 0em' }}>
      <Container textAlign='center'>
        <Grid divided inverted stackable>
          <Grid.Column width={3}>
            <Header inverted as='h4' content='Group 1' />
            <List link inverted>
              <List.Item as='a'>Link One</List.Item>
              <List.Item as='a'>Link Two</List.Item>
              <List.Item as='a'>Link Three</List.Item>
              <List.Item as='a'>Link Four</List.Item>
            </List>
          </Grid.Column>
          <Grid.Column width={3}>
            <Header inverted as='h4' content='Group 2' />
            <List link inverted>
              <List.Item as='a'>Link One</List.Item>
              <List.Item as='a'>Link Two</List.Item>
              <List.Item as='a'>Link Three</List.Item>
              <List.Item as='a'>Link Four</List.Item>
            </List>
          </Grid.Column>
          <Grid.Column width={3}>
            <Header inverted as='h4' content='Group 3' />
            <List link inverted>
              <List.Item as='a'>Link One</List.Item>
              <List.Item as='a'>Link Two</List.Item>
              <List.Item as='a'>Link Three</List.Item>
              <List.Item as='a'>Link Four</List.Item>
            </List>
          </Grid.Column>
          <Grid.Column width={7}>
            <Header inverted as='h4' content='Footer Header' />
            <p>
              Extra space for a call to action inside the footer that could help re-engage users.
            </p>
          </Grid.Column>
        </Grid>

        <Divider inverted section />
        <Image centered size='mini' src='/logo.png' />
        <List horizontal inverted divided link size='small'>
          <List.Item as='a' href='#'>
            Site Map
          </List.Item>
          <List.Item as='a' href='#'>
            Contact Us
          </List.Item>
          <List.Item as='a' href='#'>
            Terms and Conditions
          </List.Item>
          <List.Item as='a' href='#'>
            Privacy Policy
          </List.Item>
        </List>
      </Container>
    </Segment>
      </div>
    );  
    
}

export default PokemonDetail
